package com.tyust.web.action.unit;

import java.io.IOException;
import java.io.PrintWriter;
import java.util.List;

import javax.servlet.http.HttpServletRequest;

import org.apache.struts2.ServletActionContext;

import com.tyust.bean.unit.TBaseUnitInfo;
import com.tyust.service.unit.UnitService;

/** 
 * MyEclipse Struts
 * Creation date: 07-20-2010
 * 
 * XDoclet definition:
 * @struts.action validate="true"
 * 查询相同的机构名称
 */
public class FindSameUnitNameAction {
	//机构服务层接口
	private UnitService unitService;
	/*
	 * Generated Methods
	 */

	public UnitService getUnitService() {
		return unitService;
	}

	public void setUnitService(UnitService unitService) {
		this.unitService = unitService;
	}

	/** 
	 * Method execute
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public String execute() {
		try {
			HttpServletRequest request=ServletActionContext.getRequest();
			PrintWriter printWriter = ServletActionContext.getResponse().getWriter();		
			TBaseUnitInfo unitInfo = new TBaseUnitInfo();

				unitInfo.setUpUnitId(request.getParameter("upUnitId").trim());
				unitInfo.setUnitName(request.getParameter("unitName").trim());
				List list = unitService.findUnit(unitInfo);
				if( list.size() > 0){
					printWriter.print(1);
				}else{
					printWriter.print(0);
				}					
		} catch (IOException e) {
			e.printStackTrace();
		}
		return null;
	}
}