package com.tyust.web.action.user;

import javax.servlet.http.HttpServletRequest;

import org.apache.struts2.ServletActionContext;

import com.tyust.common.OpertionType;
import com.tyust.common.SaveLog;
import com.tyust.service.user.UserService;

/**
 * MyEclipse Struts
 * Creation date: 07-09-2010
 *
 * XDoclet definition:
 * @struts.action path="/delUser" name="delUserForm" input="/form/delUser.jsp" scope="request" validate="true"
 */
public class DelUserAction {
	//用户服务层接口
	private UserService userService;
	public UserService getUserService() {
		return userService;
	}

	public void setUserService(UserService userService) {
		this.userService = userService;
	}

	public String getIds() {
		return ids;
	}

	public void setIds(String ids) {
		this.ids = ids;
	}

	private String ids;
	/*
	 * Generated Methods
	 */

	/**
	 * Method execute
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public String execute() {
		HttpServletRequest request=ServletActionContext.getRequest();
		try{
			 String[] userids=ids.trim().split(",");
			for(int i=0;i<userids.length;i++){
				if(userService.selectIsExistUserRole(userids[i])>0){
					userService.deleteUserRole(userids[i]);
				}
				userService.deleteUser(userids[i]);
				SaveLog.saveLog(request, "用户管理", OpertionType.State.DELETE, "删除用户ID为"+userids[i]+"的用户成功");
			}
		}catch(Exception e){
			/**
			 * TODO 删除用户如果出错了那么返回error;
			 */
			e.printStackTrace();
			SaveLog.saveLog(request, "用户管理", OpertionType.State.DELETE, e.toString());
		}
		finally{
		//dm.endTransaction();
		}
		return "success";
	}
}